// DOM Elements
const storyGrid = document.getElementById('story-grid');
const modal = document.getElementById('story-modal');
const closeModal = document.querySelector('.close-btn');
const modalTitle = document.getElementById('modal-title');
const modalStory = document.getElementById('modal-story');
const modalMoral = document.getElementById('modal-moral');
const modalIcon = document.getElementById('modal-icon');

// à¦—à¦²à§à¦ª à¦²à§‹à¦¡ à¦•à¦°à¦¾à¦° à¦«à¦¾à¦‚à¦¶à¦¨
function loadStories() {
    storyGrid.innerHTML = '';
    stories.forEach(story => {
        const card = document.createElement('div');
        card.classList.add('card');
        card.innerHTML = `
            <div class="card-icon">${story.icon}</div>
            <h3 class="card-title">${story.title}</h3>
        `;
        card.addEventListener('click', () => openStory(story));
        storyGrid.appendChild(card);
    });
}

// à¦—à¦²à§à¦ª à¦–à§‹à¦²à¦¾à¦° à¦«à¦¾à¦‚à¦¶à¦¨
function openStory(story) {
    modalTitle.innerText = story.title;
    modalStory.innerText = story.story;
    modalMoral.innerText = story.moral;
    modalIcon.innerText = story.icon;
    modal.style.display = 'block';
    document.body.style.overflow = 'hidden'; // à¦¸à§à¦•à§à¦°à¦² à¦¬à¦¨à§à¦§ à¦•à¦°à¦¾
}

// à¦®à§‹à¦¡à¦¾à¦² à¦¬à¦¨à§à¦§ à¦•à¦°à¦¾à¦° à¦«à¦¾à¦‚à¦¶à¦¨
function closeStoryModal() {
    modal.style.display = 'none';
    document.body.style.overflow = 'auto'; // à¦¸à§à¦•à§à¦°à¦² à¦šà¦¾à¦²à§ à¦•à¦°à¦¾
}

// à¦‡à¦­à§‡à¦¨à§à¦Ÿ à¦²à¦¿à¦¸à§‡à¦¨à¦¾à¦°
closeModal.addEventListener('click', closeStoryModal);

// à¦¬à¦¾à¦‡à¦°à§‡ à¦•à§à¦²à¦¿à¦• à¦•à¦°à¦²à§‡ à¦¬à¦¨à§à¦§ à¦¹à¦¬à§‡
window.addEventListener('click', (e) => {
    if (e.target === modal) {
        closeStoryModal();
    }
});

// à¦ªà§‡à¦œ à¦²à§‹à¦¡ à¦¹à¦²à§‡ à¦—à¦²à§à¦ª à¦¦à§‡à¦–à¦¾à¦“
document.addEventListener('DOMContentLoaded', loadStories);

// Service Worker à¦°à§‡à¦œà¦¿à¦¸à§à¦Ÿà§à¦°à§‡à¦¶à¦¨ (PWA à¦à¦° à¦œà¦¨à§à¦¯)
if ('serviceWorker' in navigator) {
    navigator.serviceWorker.register('/sw.js').then(reg => {
        console.log('Service Worker Registered');
    }).catch(err => {
        console.log('Service Worker Error', err);
    });
}
